import styled from 'styled-components';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { createGlobalStyle } from 'styled-components';

import '../../App.css';
import Table from '../ui/Table';
import HeaderComponent from '../ui/HeaderComponent';
import { animationMixin } from '../effect/Animation';
// ${animationMixin}; -> Ïï†ÎãàÎ©îÏù¥ÏÖò Ìö®Í≥ºÎ•º ÏúÑÌïú ÏΩîÎìú

const AllGlobalStyle = createGlobalStyle`
  @font-face {
    font-family: 'Pretendard-ExtraBold';
    src: url('/font/Pretendard-ExtraBold.ttf') format('truetype');
  }
    body {
    font-family: 'Pretendard-ExtraBold';
    }
  @font-face {
    font-family: 'Pretendard-Medium';
    src: url('/font/Pretendard-Medium.ttf') format('truetype');;
  }
 `;

/* Î†àÏù¥ÏïÑÏõÉ ÏΩîÎìú */
const Wrapper = styled.div`
  padding-left: 180px;
  padding-right: 180px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: auto;
  ${animationMixin};
`;

const SecondWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 80%;
  padding-top: 30px;
`;

const NewsWrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding-top: 10px;
  padding-bottom: 30px;
  width: 100%;
  ${animationMixin};
`;

const PaperWrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding-top: 10px;
  width: 100%;
  ${animationMixin};
`;


const HeaderBox = styled.div`
    border-radius: 15px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 40px; /* ÎÜíÏù¥ Ï°∞Ï†ï */
    width: 100px;
    margin-left: 5px;
    margin-right: 5px;
`;

const HeaderBoxText = styled.p`
  font-size: 16px;
  text-align: center;
  color: #252a2f;
  font-family: 'Pretendard-ExtraBold';
`;

const HeaderBoxTextNone = styled.p`
  font-size: 16px;
  text-align: center;
  color: #8c8c8c;
  font-family: 'Pretendard-ExtraBold';
`;

const ClickableBox = styled(HeaderBox)`
  cursor: pointer;
  transition: background-color 0.3s ease;
  &:hover {
    background-color: #dcdcdc;
  }
`;




/* ÌéòÏù¥ÏßÄ Ï†úÎ™© */
const MainTitleText = styled.p`
    font-size: 46px;
    font-weight: bold;
    text-align: center;
    margin-bottom: 0px;
    padding-bottom: 0px;
    color: #252a2f;
    font-family: 'Pretendard-ExtraBold';
`;

const HighlightText = styled.span`
    color: #252a2f;
    background: linear-gradient(to right, #d3e8f7, #e9dcf9);
    border-radius: 15px;
    padding-left: 10px;
    padding-right: 10px;
    font-family: 'Pretendard-ExtraBold';
`;



/* Î≥∏Î¨∏ */
const SubText = styled.p`
  font-size: 20px;
  //text-align: center;
  color: #252a2f;
  font-family: 'Pretendard-ExtraBold';
`;

const CustomTable = styled(Table)`
  width: 100%;
  height: 200px;
`;



const ResearchPageSub = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { message } = location.state || {}; // Default to an empty object if state is undefined
  const [fetchedNews, setFetchedNews] = useState([]);
  const [papersData, setPapersData] = useState([]);



  useEffect(() => {
    // Send a request to the backend to fetch NEWS
    const fetchNewsData = async () => {
      try {
        const keywordsArray = JSON.parse(message);

        const response = await fetch('http://localhost:8000/research-page-sub-news', {
          method: 'POST', // or 'GET', depending on your backend setup
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ inputValue: keywordsArray }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const news = await response.json();

        setFetchedNews(news.message);
      } catch (error) {
        console.error('Error from news endpoint:', error);
      }
    };


    // Send a request to the backend to fetch PAPERS
    const fetchPapersData = async () => {
      try {
        const keywordsArray = JSON.parse(message);

        const response = await fetch('http://localhost:8000/research-page-sub-papers', {
          method: 'POST', // or 'GET', depending on your backend setup
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ inputValue: keywordsArray }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const papersResponse = await response.json();
        if (typeof papersResponse.message === 'string') {
          const parsedPapers = JSON.parse(papersResponse.message);
          setPapersData(parsedPapers);
        } else {
          setPapersData(papersResponse.message);
        }
      } catch (error) {
        console.error('Error from papers endpoint:', error);
      }
    };



    // Check if message is not empty
    if (message) {
      fetchNewsData();
      fetchPapersData();
    }
  }, [message]);


  // ÌéòÏù¥ÏßÄ Ïù¥Îèô Ìï®Ïàò
  const navigateTo = (path) => {
    console.log(`${path} clicked!`);
    navigate(path);
  };


  /* Îâ¥Ïä§ ÌÖåÏù¥Î∏î Ïª¥Ìè¨ÎÑåÌä∏Ïóê ÏÇ¨Ïö©Ìï† Ïª¨ÎüºÎ™Ö */
  const columns_news = React.useMemo(
    () => [
      { Header: 'Î≤àÌò∏', accessor: 'number' },
      {
        Header: 'Í∏∞ÏÇ¨ Ï†úÎ™©',
        accessor: 'title_news',
        Cell: ({ row }) => (
          <a href={row.original.url_news} target="_blank" rel="noopener noreferrer" className="link-style">
            {row.values.title_news}
          </a>
        )
      }
    ],
    []
  );

  /* Îâ¥Ïä§ Îç∞Ïù¥ÌÑ∞ */
  const data_news = React.useMemo(() => {
    return fetchedNews.map((item, index) => ({
      number: (index + 1).toString(),
      title_news: item[0],
      url_news: item[1]
    }));
  }, [fetchedNews]);

  /* ÎÖºÎ¨∏ ÌÖåÏù¥Î∏î Ïª¥Ìè¨ÎÑåÌä∏Ïóê ÏÇ¨Ïö©Ìï† Ïª¨ÎüºÎ™Ö */
  const columns_paper = React.useMemo(
    () => [
      { Header: 'Î≤àÌò∏', accessor: 'number' },
      {
        Header: 'ÎÖºÎ¨∏ Ï†úÎ™©',
        accessor: 'title_paper',
        Cell: ({ row }) => (
          <a href={row.original.url_paper} target="_blank" rel="noopener noreferrer" className="link-style">
            {row.values.title_paper}
          </a>
        )
      }
    ],
    []
  );

  /* ÎÖºÎ¨∏ ÏûÑÏãú Îç∞Ïù¥ÌÑ∞ */
  const data_paper = React.useMemo(() => {
    return papersData.map((item, index) => ({
      number: (index + 1).toString(),
      title_paper: item[0], // Assuming this is the title
      url_paper: item[3] // Assuming this is the URL
    }));
  }, [papersData]);


  return (
    <div>
      <AllGlobalStyle />
      <HeaderComponent>
        {/* HeaderComponentÏùò RightContainerÏóê Îì§Ïñ¥Í∞à ÎÇ¥Ïö©ÏùÑ childrenÏúºÎ°ú Ï†ÑÎã¨ */}
        <ClickableBox onClick={() => navigateTo('/spec-page')}>
          <HeaderBoxTextNone>Î™ÖÏÑ∏ÏÑú ÏûëÏÑ±</HeaderBoxTextNone>
        </ClickableBox>
        <ClickableBox onClick={() => navigateTo('/sim-page')}>
          <HeaderBoxTextNone>Ïú†ÏÇ¨ÎèÑ Î∂ÑÏÑù</HeaderBoxTextNone>
        </ClickableBox>
        <ClickableBox onClick={() => navigateTo('/research-page-main')}>
          <HeaderBoxText>Ïó∞Íµ¨ÎèôÌñ•</HeaderBoxText>
        </ClickableBox>
      </HeaderComponent>
      <Wrapper>
        <MainTitleText>üî• Ï∞∏Í≥†ÌïòÎ©¥ Ï¢ãÏùÑ <HighlightText> Íµ≠ÎÇ¥ ÎÖºÎ¨∏/Îâ¥Ïä§</HighlightText> Top 5 ÏóêÏöî</MainTitleText>
        <SecondWrapper>
          <NewsWrapper>
            <SubText style={{ textAlign: 'left' }}>üì∞ Íµ≠ÎÇ¥ Îâ¥Ïä§</SubText>
            <CustomTable columns={columns_news} data={data_news.slice(0, 5)} />
          </NewsWrapper>
          <PaperWrapper>
            <SubText style={{ textAlign: 'right' }}>üìÑ Íµ≠ÎÇ¥ ÎÖºÎ¨∏</SubText>
            <CustomTable columns={columns_paper} data={data_paper.slice(0, 5)} />
          </PaperWrapper>
        </SecondWrapper>
      </Wrapper>
    </div>
  );
};

export default ResearchPageSub;
