import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { createGlobalStyle } from 'styled-components';

import ImgUpload from '../ui/ImgUpload';
import TextInput from '../ui/TextInput';

const AllGlobalStyle = createGlobalStyle`
  @font-face {
    font-family: 'Pretendard';
    src: url('/font/Pretendard-ExtraBold.ttf') format('truetype');
  }
    body {
    font-family: 'Pretendard', sans-serif;
    }
 `;


// Ï†ÑÏ≤¥Ï†ÅÏù∏ Î†àÏù¥ÏïÑÏõÉÏùÑ Îã¥ÎãπÌïòÎäî ÏΩîÎìú
const Wrapper = styled.div`
  padding: 30px;
  display: flex;
  flex-direction: column;
  height: auto;
  padding-top: 50px;
`;

const SecondWrapper = styled.div`
  padding: 30px;
  display: flex;
  flex-direction: row; /* Change from 'column' to 'row' */
  justify-content: space-around; /* Adjust horizontal alignment as needed */
  height: auto;
  padding-top: 30px;
`;

const Header = styled.p`
  height: 60px;
  background-color: #252a2f;
  color: white;
  background-color: #252a2f;
  justify-content: center;
  align-items: left;
  `;

const Icon = styled.img`
  margin-left: 0px;
  max-width: 100px;
`;

// Ï†úÎ™© Îã¥Îãπ
const MainTitleText = styled.p`
    font-size: 46px;
    font-weight: bold;
    text-align: center;
    margin-bottom: 0px;
    padding-bottom: 0px;
`;

const HighlightText = styled.span`
    color: #FFFFFF;
    background: linear-gradient(to right, #9dbdeb, #7f85d8);
    border-radius: 15px;
    padding-left: 10px;
    padding-right: 10px;
`;

const SubTitleText = styled.p`
    font-size: 28px;
    font-weight: bold;
    text-align: center;
    margin-bottom: -5px;
    padding-bottom: 60px;
`;

const BoxText = styled.p`
  font-size: 20px;
  text-align: center;
`;


const Box = styled.div`
    padding-bottom: 30px;
    background-color: white;
    border-radius: 15px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    min-height: 400px;
    min-width: 420px;
    box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.1); /* Í∑∏Î¶ºÏûê Ìö®Í≥º Ï∂îÍ∞Ä */
`;

const ClickableBox = styled(Box)`
  cursor: pointer;
  transition: background-color 0.3s ease; // Î∞∞Í≤Ω ÏÉâÏÉÅ Î≥ÄÌôîÏóê Ïï†ÎãàÎ©îÏù¥ÏÖò Ìö®Í≥ºÎ•º Ï∂îÍ∞ÄÌï©ÎãàÎã§.
  &:hover {
    background-color: #dcdcdc; // Ïó¨Í∏∞ÏÑú ÏõêÌïòÎäî ÏÉâÏÉÅÏúºÎ°ú Î∞îÍøâÎãàÎã§.
  }
`;


const SpecPage = () => {
  const navigate = useNavigate();

  const handleBoxClick = (boxNumber) => {
    console.log(`Box ${boxNumber} clicked!`);
    navigate('second-page'); // ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏãú ÏÇ¨Ïö©
  };

  return (
    <div>
      <AllGlobalStyle />
      <Header></Header>
      <Wrapper>
          <MainTitleText>‚úçüèª <HighlightText>Î™ÖÏÑ∏ÏÑú ÏûëÏÑ± Î≥¥Ï°∞</HighlightText> ÌéòÏù¥ÏßÄ ÏûÖÎãàÎã§</MainTitleText>
          <ImgUpload onFileSelect={(file) => console.log(file)} />
          <TextInput></TextInput>
      </Wrapper>
    </div>
  );
};

export default SpecPage;
