import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { createGlobalStyle } from 'styled-components';
import { useState, useEffect } from 'react';
import Dialog from '../ui/Dialog';

import TextInput from '../ui/TextInput';
import Button from '../ui/Button';
import Table from '../ui/Table';
import HeaderComponent from '../ui/HeaderComponent'
import { animationMixin } from '../effect/Animation';

const AllGlobalStyle = createGlobalStyle`
  @font-face {
    font-family: 'Pretendard-ExtraBold';
    src: url('/font/Pretendard-ExtraBold.ttf') format('truetype');
  }
    body {
    font-family: 'Pretendard-ExtraBold';
    }
  @font-face {
    font-family: 'Pretendard-Medium';
    src: url('/font/Pretendard-Medium.ttf') format('truetype');;
  }
 `;

/* Î†àÏù¥ÏïÑÏõÉ ÏΩîÎìú */
const Wrapper = styled.div`
  padding-left: 180px;
  padding-right: 180px;
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 1000px;
`;

const SecondWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  width: 80%;
  height: 130px;
  padding-top: 60px;
  gap: 20px;
  ${animationMixin};
`;

const ThirdWrapper = styled.div`
  padding: 60px;
  display: flex;
  width: 100%; 
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  box-sizing: border-box;
  ${({ isVisible }) => isVisible && animationMixin}; // isVisibleÏù¥ trueÏùº ÎïåÎßå Ïï†ÎãàÎ©îÏù¥ÏÖò Ï†ÅÏö©
`;

const FourthWrapper = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
  padding-top: 20px;
`;

/* ÏÉÅÎã®Î∞î- ÏÑ†ÌÉùÎêú ÌéòÏù¥ÏßÄ Î≤ÑÌäº*/
const HeaderBox = styled.div`
    border-radius: 15px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 40px; /* ÎÜíÏù¥ Ï°∞Ï†ï */
    width: 100px;
    margin-left: 5px;
    margin-right: 5px;
`;

const HeaderBoxText = styled.p`
  font-size: 16px;
  text-align: center;
  color: #252a2f;
`;

const HeaderBoxTextNone = styled.p`
  font-size: 16px;
  text-align: center;
  color: #8c8c8c;
`;

const ClickableBox = styled(HeaderBox)`
  cursor: pointer;
  transition: background-color 0.3s ease;
  &:hover {
    background-color: #dcdcdc;
  }
`;




/* ÌéòÏù¥ÏßÄ Ï†úÎ™© */
const MainTitleText = styled.p`
    font-size: 46px;
    font-weight: bold;
    text-align: center;
    margin-bottom: 0px;
    padding-bottom: 0px;
    color: #252a2f;
    font-family: 'Pretendard-ExtraBold';
    ${animationMixin};
`;

const HighlightText = styled.span`
    color: #252a2f;
    background: linear-gradient(to right, #d3e8f7, #e9dcf9);
    border-radius: 15px;
    padding-left: 10px;
    padding-right: 10px;
    font-family: 'Pretendard-ExtraBold';
`;


/* Î≥∏Î¨∏ ÏÉÅÎã® */
const GuideText = styled.p`
  font-size: 12px;
  text-align: center;
  color: #aaaaaa;
  font-family: 'Pretendard-ExtraBold';
  ${animationMixin};
`;

const CustomButton = styled(Button)`
  padding: 7px 7px;
  font-size: 30px;
  border-width: 0px;
  border-radius: 15px;
  display: flex;
  width: 140px;
  height: 100%;
  cursor: pointer;
  align-items: center;
  background: linear-gradient(to right, #d3e8f7, #e9dcf9);
  color: white;
  justify-content: center;
  box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);
  font-family: 'Pretendard-ExtraBold';
  ${animationMixin};
`;

const CustomTextInput = styled(TextInput)`
    height: 100%;
    box-sizing: border-box; // paddingÏùÑ Ìè¨Ìï®Ìïú ÎÜíÏù¥Î°ú ÏÑ§Ï†ï
    ${animationMixin}
`;

/* Î≥∏Î¨∏ ÌïòÎã® */
const SubText = styled.p`
  font-size: 20px;
  padding-bottom: 20px;
  text-align: center;
  color: #252a2f;
  font-family: 'Pretendard-ExtraBold';
`;

const DefaultText = styled.div`
  font-size: 20px;
  padding-top: 10px;
  padding-bottom: 15px;
  text-align: center;
  color: #252a2f;
  font-family: 'Pretendard-ExtraBold';
`;


/* Î°úÎî©ÌôîÎ©¥ Ïª¥Ìè¨ÎÑåÌä∏ */
const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000; // ÏàòÏ†ïÎê®
`;


const Message = styled.p`
  color: #d0d0d0;
  display: flex;
  flex-direction: column;
  font-size: 24px; // ÏàòÏ†ïÎê®
  opacity: 0; // Ï¥àÍ∏∞ ÏÉÅÌÉúÎäî Ìà¨Î™Ö
  align-items: center;
  animation: fadeInOut 4s infinite; // 4Ï¥à ÎèôÏïà Î¨¥Ìïú Î∞òÎ≥µ
  background-color:  rgba(0, 0, 0, 0.3);
  width: 368px;
  padding: 20px;
  border-radius: 15px;
  box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);

  @keyframes fadeInOut {
    0%, 100% { opacity: 0; }
    50% { opacity: 1; }
  }
`;

  /* Íµ≠Í∞ÄÎ™ÖÏóê Îî∞Î•∏ Ïù¥Î™®Ìã∞ÏΩò Î∞òÌôò Ìï®Ïàò */
  const countryToEmoji = (country) => {
    switch (country) {
      case 'KR': return 'üá∞üá∑';
      case 'US': return 'üá∫üá∏';
      case 'CN': return 'üá®üá≥';
      case 'JP': return 'üáØüáµ';
      case 'EP': return 'üá™üá∫';
      case 'WO': return 'üåê';
      default: return '-';
    }
  };



const SimPage = () => {
  const navigate = useNavigate();
  const [isDialogOpen, setIsDialogOpen] = useState(false); // Dialog ÌëúÏãú ÏÉÅÌÉú
  const [text, setText] = useState('');
  const [domesticData, setDomesticData] = useState([]); // Íµ≠ÎÇ¥ ÌäπÌóà Îç∞Ïù¥ÌÑ∞ ÏÉÅÌÉú
  const [internationalData, setInternationalData] = useState([]); // Ìï¥Ïô∏ ÌäπÌóà Îç∞Ïù¥ÌÑ∞ ÏÉÅÌÉú
  const [selectedItemDetails, setSelectedItemDetails] = useState('');
  const hasData = domesticData.length > 0 || internationalData.length > 0;


  // ÌéòÏù¥ÏßÄ Ïù¥Îèô Ìï®Ïàò
  const navigateTo = (path) => {
    console.log(`${path} clicked!`);
    navigate(path);
  };

  // DialogÎ•º Ïó¥Í∏∞ ÏúÑÌïú Ìï®Ïàò
  const openDialog = () => {
    setIsDialogOpen(true);
  };

  // DialogÎ•º Îã´Í∏∞ ÏúÑÌïú Ìï®Ïàò
  const closeDialog = () => {
    setIsDialogOpen(false);
  };

  const handleChange = (event) => {
    // ÏÇ¨Ïö©Ïûê ÏûÖÎ†•Ïóê Îî∞Îùº ÌÖçÏä§Ìä∏Î•º ÏóÖÎç∞Ïù¥Ìä∏Ìï©ÎãàÎã§.
    setText(event.target.value);
  };

    // ÌÖåÏù¥Î∏î Ïª¥Ìè¨ÎÑåÌä∏Ïóê ÏÇ¨Ïö©Ìï† ÏûÑÏãú
  const columns = React.useMemo(
    () => [
        // Ïª¨Îüº Ï†ïÏùò ÏóÖÎç∞Ïù¥Ìä∏
      { Header: 'Î≤àÌò∏', accessor: 'number' },
      { Header: 'Íµ≠Í∞Ä', accessor: 'country', Cell: ({ value }) => (
        <span style={{ fontSize: '24px' }}>{countryToEmoji(value)}</span> // Ìè∞Ìä∏ ÌÅ¨Í∏∞Î•º 24pxÎ°ú Ï°∞Ï†ï
      )},
      { Header: 'Ï∂úÏõê Î≤àÌò∏', accessor: 'id' },
      { Header: 'Î∞úÎ™ÖÏùò Î™ÖÏπ≠', accessor: 'Î∞úÎ™ÖÏùòÎ™ÖÏπ≠'},
      { Header: 'ÎåÄÌëú Î∂ÑÎ•ò ÏΩîÎìú', accessor: 'IPC_code_only' },
      { Header: 'Ïú†ÏÇ¨ÎèÑ', accessor: 'distance' },
      {
        Header: 'ÏöîÏïΩ',
        accessor: 'details',
        // Use a custom cell renderer
        Cell: ({ row }) => (
          <button style={{ cursor: 'pointer', backgroundColor: 'transparent', border: 'none' }} onClick={() => openDetailsDialog(row.original)}>
            üîé
          </button>
        ),
      },
    ],
    []
  );

  const handleSubmit = async (e) => {
    e?.preventDefault(); // Ìèº Ï†úÏ∂ú Î∞©ÏßÄ

    setIsLoading(true);

    const requestBody = { query: text, n_results: 10 };

    try {
      const response = await fetch('http://localhost:8000/similarity-check', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      const responseData = await response.json(); // ÏÑúÎ≤Ñ ÏùëÎãµÏùÑ JSON ÌòïÌÉúÎ°ú Î≥ÄÌôò

      const transformedDomesticData = responseData.korean_results.results.map((item, index) => {
        return {
          number: index + 1,
          id: item.id,
          country: 'KR',
          Î∞úÎ™ÖÏùòÎ™ÖÏπ≠: item.Î∞úÎ™ÖÏùòÎ™ÖÏπ≠,
          distance: (1 - item.distance).toFixed(2),
          IPC_code_only: [...new Set(item.IPC_code_only.replace(/[\[\]']+/g, '').split(','))].join(', '), // Î∞∞Ïó¥ ÌëúÌòÑÏùÑ Î¨∏ÏûêÏó¥Î°ú Î≥ÄÌôò
          details: item.ÏöîÏïΩ,
        };
      });
      setDomesticData(transformedDomesticData);

      const transformedInternationalData = responseData.foreign_results.results.map((item, index) => ({
        number: index + 1,
        id: item.id,
        country: item.Íµ≠Í∞Ä,
        Î∞úÎ™ÖÏùòÎ™ÖÏπ≠: item.Î∞úÎ™ÖÏùòÎ™ÖÏπ≠,
        distance: (1 - item.distance).toFixed(2),
        IPC_code_only: item.ipc_category.replace(/[\[\]']+/g, ''), // Î∞∞Ïó¥ ÌëúÌòÑÏùÑ Î¨∏ÏûêÏó¥Î°ú Î≥ÄÌôò
        details: item.ÏöîÏïΩ,
      }));
      setInternationalData(transformedInternationalData);

    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setIsLoading(false); // Î°úÎî© ÏÉÅÌÉú Ï¢ÖÎ£å
    }
  };

  const openDetailsDialog = (item) => {
    setSelectedItemDetails(item.details); // Assume 'details' contains the summary text
    setIsDialogOpen(true);
  };


  const handleButtonClick = async () => {
    await handleSubmit(); // Call handleSubmit directly or modify to not use the event parameter
  };


  const [isLoading, setIsLoading] = useState(false); // Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  // Î°úÎî© ÌôîÎ©¥ Ïª¥Ìè¨ÎÑåÌä∏
  const LoadingOverlay = () => {
    const messages = [
      <>
      <div style={{ fontSize: '50px', marginBottom: '5px', color: '#252a2f' }}>ü§î</div>
      <div>ÏûëÏÑ±Ìï¥Ï£ºÏã† ÎÇ¥Ïö©ÏùÑ Î∂ÑÏÑùÌïòÍ≥† ÏûàÏñ¥Ïöî</div>
      </>,
      <>
      <div style={{ fontSize: '50px', marginBottom: '5px', color: '#252a2f' }}>‚åõÔ∏è</div>
      <div>nÎ∂ÑÏ†ïÎèÑ ÏÜåÏöîÎê† Ïàò ÏûàÏñ¥Ïöî</div>
      </>
    ];

    const [currentMessageIndex, setCurrentMessageIndex] = useState(0);

    useEffect(() => {
      const intervalId = setInterval(() => {
        setCurrentMessageIndex((prevIndex) => (prevIndex + 1) % messages.length);
      }, 4000); // Î©îÏãúÏßÄ Î≥ÄÍ≤Ω Ï£ºÍ∏∞Î•º 4Ï¥àÎ°ú ÏÑ§Ï†ï (Ïï†ÎãàÎ©îÏù¥ÏÖò Ï£ºÍ∏∞Ïóê ÎßûÏ∂§)

      return () => clearInterval(intervalId);
    }, []);

    return (
      <Overlay>
        <Message>{messages[currentMessageIndex]}</Message>
      </Overlay>
    );
  };



  return (
    <div>
      <AllGlobalStyle />
      <HeaderComponent>
        {/* HeaderComponentÏùò RightContainerÏóê Îì§Ïñ¥Í∞à ÎÇ¥Ïö©ÏùÑ childrenÏúºÎ°ú Ï†ÑÎã¨ */}
        <ClickableBox onClick={() => navigateTo('/spec-page')}>
          <HeaderBoxTextNone>Î™ÖÏÑ∏ÏÑú ÏûëÏÑ±</HeaderBoxTextNone>
        </ClickableBox>
        <ClickableBox onClick={() => navigateTo('/sim-page')}>
          <HeaderBoxText>Ïú†ÏÇ¨ÎèÑ Î∂ÑÏÑù</HeaderBoxText>
        </ClickableBox>
        <ClickableBox onClick={() => navigateTo('/research-page-main')}>
          <HeaderBoxTextNone>Ïó∞Íµ¨ÎèôÌñ•</HeaderBoxTextNone>
        </ClickableBox>
      </HeaderComponent>
      <Wrapper>
          <MainTitleText>üìà <HighlightText>Ïú†ÏÇ¨ÎèÑ Î∂ÑÏÑù</HighlightText> ÏùÑ ÎèÑÏôÄÎìúÎ¶¥Í≤åÏöî</MainTitleText>
          <GuideText>* ÌòÑÏû¨ ÏÑúÎπÑÏä§Îäî ÌïúÍµ≠/ÎØ∏Íµ≠/Ï§ëÍµ≠/ÏùºÎ≥∏/Ïú†ÎüΩ Îã§ÏÑØ Íµ≠Í∞ÄÏùò ÌäπÌóà Ï†ïÎ≥¥Îßå Ï†úÍ≥µÌïòÍ≥† ÏûàÏäµÎãàÎã§</GuideText>
          <SubText style={{marginTop: '40px', marginBottom:'-50px'}}>ÏïÑÏù¥ÎîîÏñ¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏãúÎ©¥, Ìïú Î≤àÏóê Íµ≠ÎÇ¥Ïô∏ Ïú†ÏÇ¨ ÌäπÌóàÎ•º Î≥¥Ïó¨ÎìúÎ†§Ïöî</SubText>
          <SecondWrapper>
            <CustomTextInput 
              placeholder={"Î∂ÑÏÑùÌï† ÏïÑÏù¥ÎîîÏñ¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"}
              value={text}
              onChange={handleChange}
            />
            <CustomButton title='üîç' onClick={handleButtonClick} /> {/* Î≤ÑÌäº ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ Ìï∏Îì§Îü¨ Ïó∞Í≤∞ */}
          </SecondWrapper>
          {hasData && (
          <ThirdWrapper isVisible={hasData}>
            <SubText>ÎãπÏã†Ïùò ÏïÑÏù¥ÎîîÏñ¥Î•º Î∂ÑÏÑùÌïú Í≤∞Í≥º, Ïú†ÏÇ¨Ìïú ÌäπÌóàÎäî ÏïÑÎûòÏôÄ Í∞ôÏïÑÏöî</SubText>
            <FourthWrapper>
              <DefaultText>üá∞üá∑ Íµ≠ÎÇ¥ ÌäπÌóà</DefaultText>
              <Table columns={columns} data={domesticData} />
            </FourthWrapper>
            <FourthWrapper>
              <DefaultText>üåé Ìï¥Ïô∏ ÌäπÌóà</DefaultText>
              <Table columns={columns} data={internationalData} />
            </FourthWrapper>
          </ThirdWrapper>
          )}
      </Wrapper>
      {isLoading && <LoadingOverlay />}
      {isDialogOpen && (
        <Dialog
          title="Î∞úÎ™Ö ÏöîÏïΩ Ï†ïÎ≥¥"
          confirmText="ÌôïÏù∏"
          onCancel={() => setIsDialogOpen(false)}
          onConfirm={() => setIsDialogOpen(false)}
          visible={isDialogOpen}
        >
          {selectedItemDetails}
        </Dialog>
      )}
    </div>
  );
};

export default SimPage;
